import React, { useState, useEffect } from 'react';
import axios from '../Axios/axios';
import AdminUsersTable from './AdminUsersTable/AdminUsersTable';
import { NavLink } from 'react-router-dom';

function AdminPanel() {
  const [users, setUsers] = useState([]);
  

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get('/admin/getUsers', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('authToken')}`,
          },
        });
        setUsers(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchUsers();
  }, []);

  const deleteUser = async (userId) => {
    try {
      await axios.delete(`/admin/deleteUser/${userId}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('authToken')}`,
        },
      });
      setUsers((prevUsers) => prevUsers.filter((user) => user._id !== userId));
    } catch (error) {
      console.error(error);
    }
  };
  

  return ( 
    <div className=' flex-grow'>
        <nav>
            <ul className='flex gap-3 justify-between p-3 bg-slate-400 rounded-lg shadow-2xl'>
                <li>
                    <NavLink to="/">All Task</NavLink>
                </li>
                <li>
                    <NavLink to="/active">Active</NavLink>
                </li>
                <li>
                    <NavLink to="/completed">Completed</NavLink>
                </li>
            </ul>
        </nav>
    </div>
 );
}
export default AdminPanel;
